import React, { useState, useEffect } from 'react';

const StarWarsGalaxyMap = () => {
  const [planets, setPlanets] = useState([]);
  const [selectedPlanet, setSelectedPlanet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [loginForm, setLoginForm] = useState({ username: '', password: '' });
  const [loginErrors, setLoginErrors] = useState({});
  const [showLogin, setShowLogin] = useState(false);
  const [quiz, setQuiz] = useState({
    currentQuestion: 0,
    score: 0,
    questions: [
      {
        question: "Яка планета є батьківщиною джедаїв?",
        options: ["Tatooine", "Coruscant", "Tython", "Naboo"],
        correct: 2
      },
      {
        question: "На якій планеті народився Люк Скайвокер?",
        options: ["Alderaan", "Tatooine", "Dagobah", "Hoth"],
        correct: 1
      },
      {
        question: "Столиця Галактичної Республіки:",
        options: ["Naboo", "Kamino", "Coruscant", "Geonosis"],
        correct: 2
      }
    ],
    selectedAnswer: null,
    showResults: false,
    completed: false
  });
  const [surveyData, setSurveyData] = useState({
    favoriteFaction: '',
    visitedPlanets: [],
    galaxyKnowledge: ''
  });

  // Імітація отримання даних з API (SWAPI)
  const fetchPlanets = async () => {
    try {
      setLoading(true);
      // Симуляція API запиту до Star Wars API
      const mockPlanets = [
        {
          name: "Tatooine",
          climate: "Arid",
          terrain: "Desert",
          population: "200000",
          diameter: "10465",
          orbital_period: "304",
          rotation_period: "23",
          surface_water: "1",
          gravity: "1 standard",
          description: "Пустельна планета з двома сонцями, батьківщина Анакіна та Люка Скайвокерів."
        },
        {
          name: "Coruscant",
          climate: "Temperate",
          terrain: "Cityscape, mountains",
          population: "1000000000000",
          diameter: "12240",
          orbital_period: "368",
          rotation_period: "24",
          surface_water: "Unknown",
          gravity: "1 standard",
          description: "Планета-місто, столиця Галактичної Республіки та центр політичної влади."
        },
        {
          name: "Naboo",
          climate: "Temperate",
          terrain: "Grassy hills, swamps, forests, mountains",
          population: "4500000000",
          diameter: "12120",
          orbital_period: "312",
          rotation_period: "26",
          surface_water: "12",
          gravity: "1 standard",
          description: "Мирна планета з красивими містами, батьківщина королеви Амідали."
        },
        {
          name: "Hoth",
          climate: "Frozen",
          terrain: "Tundra, ice caves, mountain ranges",
          population: "Unknown",
          diameter: "7200",
          orbital_period: "549",
          rotation_period: "23",
          surface_water: "100",
          gravity: "1.1 standard",
          description: "Крижана планета, де розташовувалася база повстанців."
        },
        {
          name: "Dagobah",
          climate: "Murky",
          terrain: "Swamp, jungles",
          population: "Unknown",
          diameter: "8900",
          orbital_period: "341",
          rotation_period: "22",
          surface_water: "8",
          gravity: "N/A",
          description: "Болотиста планета, де жив у вигнанні майстер Йода."
        }
      ];
      
      setTimeout(() => {
        setPlanets(mockPlanets);
        setLoading(false);
      }, 1000);
    } catch (error) {
      console.error('Помилка завантаження планет:', error);
      setLoading(false);
    }
  };

  // Періодичне оновлення даних (AJAX)
  useEffect(() => {
    fetchPlanets();
    
    const interval = setInterval(() => {
      console.log('Оновлення даних...');
      fetchPlanets();
    }, 30000); // оновлення кожні 30 секунд

    return () => clearInterval(interval);
  }, []);

  // Валідація форми входу
  const validateLogin = () => {
    const errors = {};
    
    if (!loginForm.username.trim()) {
      errors.username = "Ім'я користувача не може бути пустим";
    } else if (loginForm.username.length < 3) {
      errors.username = "Ім'я користувача повинно містити мінімум 3 символи";
    }
    
    if (!loginForm.password.trim()) {
      errors.password = "Пароль не може бути пустим";
    } else if (loginForm.password.length < 6) {
      errors.password = "Пароль повинен містити мінімум 6 символів";
    }
    
    setLoginErrors(errors);
    return Object.keys(errors).length === 0;
  };

  // Обробка входу
  const handleLogin = () => {
    if (validateLogin()) {
      // Імітація успішного входу
      setUser({ username: loginForm.username, role: 'jedi' });
      setShowLogin(false);
      setLoginForm({ username: '', password: '' });
    }
  };

  // Обробка вікторини
  const handleQuizAnswer = (answerIndex) => {
    setQuiz(prev => ({
      ...prev,
      selectedAnswer: answerIndex
    }));
  };

  const submitQuizAnswer = () => {
    if (quiz.selectedAnswer === null) return;
    
    const isCorrect = quiz.selectedAnswer === quiz.questions[quiz.currentQuestion].correct;
    const newScore = isCorrect ? quiz.score + 1 : quiz.score;
    
    if (quiz.currentQuestion < quiz.questions.length - 1) {
      setQuiz(prev => ({
        ...prev,
        currentQuestion: prev.currentQuestion + 1,
        score: newScore,
        selectedAnswer: null
      }));
    } else {
      setQuiz(prev => ({
        ...prev,
        score: newScore,
        completed: true,
        showResults: true
      }));
    }
  };

  const resetQuiz = () => {
    setQuiz(prev => ({
      ...prev,
      currentQuestion: 0,
      score: 0,
      selectedAnswer: null,
      completed: false,
      showResults: false
    }));
  };

  return (
    <div className="container-fluid bg-dark text-light min-vh-100">
      {/* Header */}
      <nav className="navbar navbar-expand-lg navbar-dark bg-primary mb-4">
        <div className="container">
          <span className="navbar-brand h1 mb-0">🌌 Мапа Галактики Зоряних Війн</span>
          <div className="navbar-nav ms-auto">
            {user ? (
              <div className="d-flex align-items-center">
                <span className="text-light me-3">Привіт, {user.username}!</span>
                <button 
                  className="btn btn-outline-light btn-sm"
                  onClick={() => setUser(null)}
                >
                  Вийти
                </button>
              </div>
            ) : (
              <button 
                className="btn btn-outline-light"
                onClick={() => setShowLogin(true)}
              >
                Увійти
              </button>
            )}
          </div>
        </div>
      </nav>

      <div className="container">
        {/* Login Modal */}
        {showLogin && (
          <div className="modal d-block" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
            <div className="modal-dialog">
              <div className="modal-content bg-dark text-light">
                <div className="modal-header border-secondary">
                  <h5 className="modal-title">Вхід у систему</h5>
                  <button 
                    type="button" 
                    className="btn-close btn-close-white"
                    onClick={() => setShowLogin(false)}
                  ></button>
                </div>
                <div>
                  <div className="modal-body">
                    <div className="mb-3">
                      <label className="form-label">Ім'я користувача</label>
                      <input
                        type="text"
                        className={`form-control bg-secondary text-light ${loginErrors.username ? 'is-invalid' : ''}`}
                        value={loginForm.username}
                        onChange={(e) => setLoginForm(prev => ({...prev, username: e.target.value}))}
                        placeholder="Введіть ім'я користувача"
                      />
                      {loginErrors.username && (
                        <div className="invalid-feedback">{loginErrors.username}</div>
                      )}
                    </div>
                    <div className="mb-3">
                      <label className="form-label">Пароль</label>
                      <input
                        type="password"
                        className={`form-control bg-secondary text-light ${loginErrors.password ? 'is-invalid' : ''}`}
                        value={loginForm.password}
                        onChange={(e) => setLoginForm(prev => ({...prev, password: e.target.value}))}
                        placeholder="Введіть пароль"
                      />
                      {loginErrors.password && (
                        <div className="invalid-feedback">{loginErrors.password}</div>
                      )}
                    </div>
                  </div>
                  <div className="modal-footer border-secondary">
                    <button type="button" className="btn btn-secondary" onClick={() => setShowLogin(false)}>
                      Скасувати
                    </button>
                    <button 
                      type="button" 
                      className="btn btn-primary"
                      onClick={handleLogin}
                    >
                      Увійти
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Main Content */}
        <div className="row">
          {/* Planets List */}
          <div className="col-lg-8">
            <div className="card bg-secondary text-light mb-4">
              <div className="card-header d-flex justify-content-between align-items-center">
                <h3 className="mb-0">Планети Галактики</h3>
                <button className="btn btn-outline-light btn-sm" onClick={fetchPlanets}>
                  🔄 Оновити
                </button>
              </div>
              <div className="card-body">
                {loading ? (
                  <div className="text-center py-4">
                    <div className="spinner-border text-primary" role="status">
                      <span className="visually-hidden">Завантаження...</span>
                    </div>
                    <p className="mt-2">Завантаження планет...</p>
                  </div>
                ) : (
                  <div className="row">
                    {planets.map((planet, index) => (
                      <div key={index} className="col-md-6 mb-3">
                        <div className="card bg-dark border-primary h-100 planet-card">
                          <div className="card-body">
                            <h5 className="card-title text-warning">{planet.name}</h5>
                            <div className="mb-2">
                              <small className="text-muted">Клімат:</small> {planet.climate}<br/>
                              <small className="text-muted">Ландшафт:</small> {planet.terrain}<br/>
                              <small className="text-muted">Населення:</small> {planet.population}
                            </div>
                            <button 
                              className="btn btn-primary btn-sm"
                              onClick={() => setSelectedPlanet(planet)}
                            >
                              Детальніше 🪐
                            </button>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>

            {/* Detailed Planet Info */}
            {selectedPlanet && (
              <div className="card bg-secondary text-light mb-4">
                <div className="card-header d-flex justify-content-between align-items-center">
                  <h4 className="mb-0 text-warning">{selectedPlanet.name}</h4>
                  <button 
                    className="btn btn-outline-light btn-sm"
                    onClick={() => setSelectedPlanet(null)}
                  >
                    ✕
                  </button>
                </div>
                <div className="card-body">
                  <p className="lead">{selectedPlanet.description}</p>
                  <div className="row">
                    <div className="col-md-6">
                      <h6 className="text-info">Основні характеристики:</h6>
                      <ul className="list-unstyled">
                        <li><strong>Діаметр:</strong> {selectedPlanet.diameter} км</li>
                        <li><strong>Орбітальний період:</strong> {selectedPlanet.orbital_period} днів</li>
                        <li><strong>Період обертання:</strong> {selectedPlanet.rotation_period} годин</li>
                      </ul>
                    </div>
                    <div className="col-md-6">
                      <h6 className="text-info">Умови:</h6>
                      <ul className="list-unstyled">
                        <li><strong>Гравітація:</strong> {selectedPlanet.gravity}</li>
                        <li><strong>Поверхнева вода:</strong> {selectedPlanet.surface_water}%</li>
                        <li><strong>Клімат:</strong> {selectedPlanet.climate}</li>
                      </ul>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Sidebar */}
          <div className="col-lg-4">
            {/* Quiz Section */}
            <div className="card bg-secondary text-light mb-4 quiz-container">
              <div className="card-header">
                <h5 className="mb-0">🎯 Вікторина з Зоряних Війн</h5>
              </div>
              <div className="card-body">
                {!quiz.completed ? (
                  <>
                    <div className="mb-3">
                      <span className="badge bg-primary">
                        Питання {quiz.currentQuestion + 1} з {quiz.questions.length}
                      </span>
                      <span className="badge bg-success ms-2">
                        Рахунок: {quiz.score}
                      </span>
                    </div>
                    <h6 className="mb-3">{quiz.questions[quiz.currentQuestion].question}</h6>
                    <div className="mb-3">
                      {quiz.questions[quiz.currentQuestion].options.map((option, index) => (
                        <div key={index} className="form-check mb-2">
                          <input
                            className="form-check-input"
                            type="radio"
                            name="quizAnswer"
                            id={`option${index}`}
                            checked={quiz.selectedAnswer === index}
                            onChange={() => handleQuizAnswer(index)}
                          />
                          <label className="form-check-label" htmlFor={`option${index}`}>
                            {option}
                          </label>
                        </div>
                      ))}
                    </div>
                    <button 
                      className="btn btn-primary"
                      onClick={submitQuizAnswer}
                      disabled={quiz.selectedAnswer === null}
                    >
                      {quiz.currentQuestion < quiz.questions.length - 1 ? 'Далі' : 'Завершити'}
                    </button>
                  </>
                ) : (
                  <div className="text-center quiz-result">
                    <h5 className="text-white">Вікторину завершено! 🎉</h5>
                    <p className="lead text-white">
                      Ваш результат: {quiz.score} з {quiz.questions.length}
                      <br/>
                      ({Math.round((quiz.score / quiz.questions.length) * 100)}%)
                    </p>
                    <button className="btn btn-light" onClick={resetQuiz}>
                      Пройти знову
                    </button>
                  </div>
                )}
              </div>
            </div>

            {/* Survey Section */}
            <div className="card bg-secondary text-light mb-4">
              <div className="card-header">
                <h5 className="mb-0">📊 Опитування</h5>
              </div>
              <div className="card-body">
                <div className="mb-3">
                  <label className="form-label">Улюблена фракція:</label>
                  <select 
                    className="form-select bg-dark text-light"
                    value={surveyData.favoriteFaction}
                    onChange={(e) => setSurveyData(prev => ({...prev, favoriteFaction: e.target.value}))}
                  >
                    <option value="">Оберіть фракцію</option>
                    <option value="jedi">Джедаї</option>
                    <option value="sith">Сіти</option>
                    <option value="rebels">Повстанці</option>
                    <option value="empire">Імперія</option>
                    <option value="republic">Республіка</option>
                  </select>
                </div>

                <div className="mb-3">
                  <label className="form-label">Відвідані планети:</label>
                  {['Tatooine', 'Coruscant', 'Naboo', 'Hoth'].map(planet => (
                    <div key={planet} className="form-check">
                      <input
                        className="form-check-input"
                        type="checkbox"
                        id={planet}
                        checked={surveyData.visitedPlanets.includes(planet)}
                        onChange={(e) => {
                          if (e.target.checked) {
                            setSurveyData(prev => ({
                              ...prev,
                              visitedPlanets: [...prev.visitedPlanets, planet]
                            }));
                          } else {
                            setSurveyData(prev => ({
                              ...prev,
                              visitedPlanets: prev.visitedPlanets.filter(p => p !== planet)
                            }));
                          }
                        }}
                      />
                      <label className="form-check-label" htmlFor={planet}>
                        {planet}
                      </label>
                    </div>
                  ))}
                </div>

                <div className="mb-3">
                  <label className="form-label">Рівень знань галактики:</label>
                  {['Падаван', 'Джедай', 'Майстер'].map(level => (
                    <div key={level} className="form-check">
                      <input
                        className="form-check-input"
                        type="radio"
                        name="galaxyKnowledge"
                        id={level}
                        checked={surveyData.galaxyKnowledge === level}
                        onChange={() => setSurveyData(prev => ({...prev, galaxyKnowledge: level}))}
                      />
                      <label className="form-check-label" htmlFor={level}>
                        {level}
                      </label>
                    </div>
                  ))}
                </div>

                {(surveyData.favoriteFaction || surveyData.visitedPlanets.length > 0 || surveyData.galaxyKnowledge) && (
                  <div className="alert alert-info">
                    <h6>Ваші відповіді:</h6>
                    {surveyData.favoriteFaction && <p><strong>Фракція:</strong> {surveyData.favoriteFaction}</p>}
                    {surveyData.visitedPlanets.length > 0 && <p><strong>Планети:</strong> {surveyData.visitedPlanets.join(', ')}</p>}
                    {surveyData.galaxyKnowledge && <p><strong>Рівень:</strong> {surveyData.galaxyKnowledge}</p>}
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer className="bg-primary text-center py-3 mt-5">
        <p className="mb-0">🌟 May the Force be with you! 🌟</p>
        <small>Дані оновлюються автоматично кожні 30 секунд</small>
      </footer>
    </div>
  );
};

export default StarWarsGalaxyMap;
