import { useState } from "react";

export default function CustomSelect({
  options = [],
  placeholder = "Select an option",
  onSelect,
}) {
  const [isOpen, setIsOpen] = useState(false);
  const [selected, setSelected] = useState("");

  const getOptionLabel = (opt) =>
    typeof opt === "string" ? opt : String(opt);

  const normalizedOptions = Array.isArray(options)
    ? options
    : Object.keys(options);

  const handleSelect = (value) => {
    setSelected(value);
    setIsOpen(false);
    onSelect?.(value);
  };

  return (
    <div className="relative w-full max-w-sm">
      {/* Selected box */}
      <div
        className="px-4 py-2 bg-gradient-to-br from-gray-900 to-black bg-opacity-70 border-2 border-yellow-500 rounded-lg text-yellow-400 cursor-pointer select-none text-center font-medium hover:text-yellow-300 hover:border-yellow-400 transition-colors duration-200"
        onClick={() => setIsOpen(!isOpen)}
      >
        {selected || placeholder}
        <span className="float-right">â–¼</span>
      </div>

      {/* Dropdown */}
      {isOpen && (
        <ul className="absolute z-10 w-full mt-2 bg-gradient-to-br from-gray-900 to-black bg-opacity-90 border-2 border-yellow-500 rounded-lg shadow-2xl p-0 m-0 list-none">
          {normalizedOptions.map((opt) => {
            const label = getOptionLabel(opt);
            return (
              <li
                key={label}
                onClick={() => handleSelect(label)}
                className="px-4 py-2 cursor-pointer font-bold text-yellow-400 hover:bg-gray-800 hover:text-yellow-300 transition-colors duration-150"
              >
                {label}
              </li>
            );
          })}
        </ul>
      )}
    </div>
  );
}
