import { useState } from "react";

export default function CustomSelect() {
  const [isOpen, setIsOpen] = useState(false);
  const [selected, setSelected] = useState("");

  const handleSelect = (faction) => {
    setSelected(faction);
    setIsOpen(false);
  };

  const factions = [
    "Order of Jedi",
    "Order of Sith",
    "Rebel Alliance",
    "Galactic Empire",
    "Galactic Republic",
  ];

  return (
    <div className="relative w-full max-w-sm">
      {/* Selected box */}
      <div
        className="px-4 py-3 bg-black bg-opacity-70 border-2 border-yellow-500 rounded-lg text-amber-200 cursor-pointer select-none text-center font-medium hover:border-yellow-400 transition-colors duration-200"
        onClick={() => setIsOpen(!isOpen)}
      >
        {selected || "Choose a faction"}
      </div>

      {/* Dropdown */}
      {isOpen && (
        <ul className="absolute z-10 w-full mt-1 bg-black border-2 border-yellow-600 rounded-lg shadow-lg max-h-60 overflow-auto">
          {factions.map((faction, index) => (
            <li
              key={index}
              onClick={() => handleSelect(faction)}
              className="px-4 py-2 cursor-pointer text-left text-amber-200 hover:bg-yellow-500 hover:text-black transition-colors duration-150"
            >
              {faction}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
