import React, { useState, useEffect } from 'react';

const StarWarsGalaxyMap = () => {
  const [planets, setPlanets] = useState([]);
  const [selectedPlanet, setSelectedPlanet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [loginForm, setLoginForm] = useState({ username: '', password: '' });
  const [loginErrors, setLoginErrors] = useState({});
  const [showLogin, setShowLogin] = useState(false);
  const [quiz, setQuiz] = useState({
    currentQuestion: 0,
    score: 0,
    questions: [
      {
        question: "Яка планета є батьківщиною джедаїв?",
        options: ["Tatooine", "Coruscant", "Tython", "Naboo"],
        correct: 2
      },
      {
        question: "На якій планеті народився Люк Скайвокер?",
        options: ["Alderaan", "Tatooine", "Dagobah", "Hoth"],
        correct: 1
      },
      {
        question: "Столиця Галактичної Республіки:",
        options: ["Naboo", "Kamino", "Coruscant", "Geonosis"],
        correct: 2
      }
    ],
    selectedAnswer: null,
    showResults: false,
    completed: false
  });
  const [surveyData, setSurveyData] = useState({
    favoriteFaction: '',
    visitedPlanets: [],
    galaxyKnowledge: ''
  });

  // Координати планет на мапі відповідно до справжньої галактичної мапи Star Wars
  // Центр галактики в координатах (50, 50)
  const planetPositions = {
    // Deep Core
    "Coruscant": { x: 50, y: 50, color: '#C0C0C0', region: 'Deep Core' },
    
    // Core Worlds
    "Alderaan": { x: 48, y: 45, color: '#87CEEB', region: 'Core Worlds' },
    
    // Inner Rim  
    "Naboo": { x: 45, y: 40, color: '#32CD32', region: 'Inner Rim' },
    
    // Mid Rim
    "Kashyyyk": { x: 42, y: 35, color: '#8B4513', region: 'Mid Rim' },
    
    // Outer Rim
    "Tatooine": { x: 65, y: 70, color: '#FFD700', region: 'Outer Rim' },
    "Hoth": { x: 40, y: 25, color: '#ADD8E6', region: 'Outer Rim' },
    "Dagobah": { x: 70, y: 75, color: '#228B22', region: 'Outer Rim' },
    "Endor": { x: 38, y: 30, color: '#228B22', region: 'Outer Rim' },
    "Jakku": { x: 62, y: 72, color: '#D2691E', region: 'Outer Rim' },
    "Mustafar": { x: 67, y: 68, color: '#FF4500', region: 'Outer Rim' },
    
    // Unknown Regions
    "Kamino": { x: 75, y: 20, color: '#4682B4', region: 'Unknown Regions' },
    "Ilum": { x: 25, y: 20, color: '#E0FFFF', region: 'Unknown Regions' }
  };

  // Імітація отримання даних з API (SWAPI)
  const fetchPlanets = async () => {
    try {
      setLoading(true);
      const mockPlanets = [
        {
          name: "Tatooine",
          climate: "Дуже сухий та спекотний",
          terrain: "Пустеля",
          population: "200000",
          diameter: "10465",
          orbital_period: "304",
          rotation_period: "23",
          surface_water: "1",
          gravity: "1 standard",
          description: "Пустельна планета з двома сонцями, батьківщина Анакіна та Люка Скайвокерів."
        },
        {
          name: "Coruscant",
          climate: "Помірний",
          terrain: "Багаторівневе місто",
          population: "1000000000000",
          diameter: "12240",
          orbital_period: "368",
          rotation_period: "24",
          surface_water: "Unknown",
          gravity: "1 standard",
          description: "Планета-місто, столиця Галактичної Республіки та центр політичної влади."
        },
        {
          name: "Naboo",
          climate: "Помірний",
          terrain: "Рівнини, гори, ліси, болота",
          population: "4500000000",
          diameter: "12120",
          orbital_period: "312",
          rotation_period: "26",
          surface_water: "12",
          gravity: "1 standard",
          description: "Мирна планета з красивими містами, батьківщина сенатора Падме Амідали."
        },
        {
          name: "Hoth",
          climate: "Дуже холодний",
          terrain: "Крижані гори, засніжені простори",
          population: "Unknown",
          diameter: "7200",
          orbital_period: "549",
          rotation_period: "23",
          surface_water: "100",
          gravity: "1.1 standard",
          description: "Крижана планета, де розташовувалася база повстанців."
        },
        {
          name: "Alderaan",
          climate: "Помірний",
          terrain: "Гори, рівнини, міста",
          population: "2000000000",
          diameter: "12500",
          orbital_period: "364",
          rotation_period: "24",
          surface_water: "40",
          gravity: "1 standard",
          description: "Мирна планета, відома своєю красою та дипломатією. Знищена Зіркою Смерті."
        },
        {
          name: "Kashyyyk",
          climate: "Тропічний",
          terrain: "Джунглі, ліси, океани",
          population: "45000000",
          diameter: "12765",
          orbital_period: "381",
          rotation_period: "26",
          surface_water: "60",
          gravity: "1 standard",
          description: "Лісиста планета, домівка вукі. Відома своїми величезними деревами."
        },
        {
          name: "Endor",
          climate: "Помірний",
          terrain: "Ліси, гори, озера",
          population: "30000000",
          diameter: "4900",
          orbital_period: "402",
          rotation_period: "18",
          surface_water: "8",
          gravity: "0.85 standard",
          description: "Лісистий супутник, домівка евоків. Місце фінальної битви з Імперією."
        },
        {
          name: "Jakku",
          climate: "Дуже сухий",
          terrain: "Пустелі",
          population: "Unknown",
          diameter: "6400",
          orbital_period: "587",
          rotation_period: "20",
          surface_water: "Unknown",
          gravity: "1 standard",
          description: "Пустельна планета з уламками кораблів після битви при Жакку."
        },
        {
          name: "Mustafar",
          climate: "Дуже гарячий",
          terrain: "Вулкани, лавові поля",
          population: "20000",
          diameter: "4200",
          orbital_period: "412",
          rotation_period: "36",
          surface_water: "0",
          gravity: "1 standard",
          description: "Вулканічна планета, де Анакін перетворився на Дарта Вейдера."
        },
        {
          name: "Kamino",
          climate: "Помірний",
          terrain: "Океан",
          population: "1000000000",
          diameter: "19720",
          orbital_period: "463",
          rotation_period: "27",
          surface_water: "100",
          gravity: "1 standard",
          description: "Океанічна планета, де було створено армію клонів для Республіки."
        },
        {
          name: "Ilum",
          climate: "Дуже холодний",
          terrain: "Крижані гори, печери",
          population: "Unknown",
          diameter: "Unknown",
          orbital_period: "Unknown",
          rotation_period: "Unknown",
          surface_water: "Unknown",
          gravity: "Unknown",
          description: "Священна планета джедаїв з кристалами для світлових мечів."
        }
      ];
      
      setTimeout(() => {
        setPlanets(mockPlanets);
        setLoading(false);
      }, 1000);
    } catch (error) {
      console.error('Помилка завантаження планет:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPlanets();
    
    const interval = setInterval(() => {
      console.log('Оновлення даних...');
      fetchPlanets();
    }, 30000);

    return () => clearInterval(interval);
  }, []);

  // Валідація форми входу
  const validateLogin = () => {
    const errors = {};
    
    if (!loginForm.username.trim()) {
      errors.username = "Ім'я користувача не може бути пустим";
    } else if (loginForm.username.length < 3) {
      errors.username = "Ім'я користувача повинно містити мінімум 3 символи";
    }
    
    if (!loginForm.password.trim()) {
      errors.password = "Пароль не може бути пустим";
    } else if (loginForm.password.length < 6) {
      errors.password = "Пароль повинен містити мінімум 6 символів";
    }
    
    setLoginErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleLogin = () => {
    if (validateLogin()) {
      setUser({ username: loginForm.username, role: 'jedi' });
      setShowLogin(false);
      setLoginForm({ username: '', password: '' });
    }
  };

  const handleQuizAnswer = (answerIndex) => {
    setQuiz(prev => ({
      ...prev,
      selectedAnswer: answerIndex
    }));
  };

  const submitQuizAnswer = () => {
    if (quiz.selectedAnswer === null) return;
    
    const isCorrect = quiz.selectedAnswer === quiz.questions[quiz.currentQuestion].correct;
    const newScore = isCorrect ? quiz.score + 1 : quiz.score;
    
    if (quiz.currentQuestion < quiz.questions.length - 1) {
      setQuiz(prev => ({
        ...prev,
        currentQuestion: prev.currentQuestion + 1,
        score: newScore,
        selectedAnswer: null
      }));
    } else {
      setQuiz(prev => ({
        ...prev,
        score: newScore,
        completed: true,
        showResults: true
      }));
    }
  };

  const resetQuiz = () => {
    setQuiz(prev => ({
      ...prev,
      currentQuestion: 0,
      score: 0,
      selectedAnswer: null,
      completed: false,
      showResults: false
    }));
  };

  const getPlanetSize = (size) => {
    // Всі планети тепер однакового розміру
    return 'w-3 h-3';
  };

  const handlePlanetClick = (planetName) => {
    const planet = planets.find(p => p.name === planetName);
    if (planet) {
      setSelectedPlanet(planet);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-black via-purple-900 to-black text-white">
      {/* Header */}
      <nav className="bg-gradient-to-r from-purple-800 to-indigo-900 shadow-lg border-b border-purple-500">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <h1 className="text-2xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-purple-300 to-pink-300">
              🌌 Мапа Галактики Зоряних Війн
            </h1>
            <div className="flex items-center space-x-4">
              {user ? (
                <div className="flex items-center space-x-3">
                  <span className="text-purple-200">Привіт, {user.username}!</span>
                  <button 
                    className="px-3 py-1 bg-purple-600 hover:bg-purple-700 rounded-lg transition-colors border border-purple-400"
                    onClick={() => setUser(null)}
                  >
                    Вийти
                  </button>
                </div>
              ) : (
                <button 
                  className="px-4 py-2 bg-gradient-to-r from-purple-600 to-indigo-600 hover:from-purple-700 hover:to-indigo-700 rounded-lg transition-all transform hover:scale-105 shadow-lg border border-purple-400"
                  onClick={() => setShowLogin(true)}
                >
                  Увійти
                </button>
              )}
            </div>
          </div>
        </div>
      </nav>

      {/* Login Modal */}
      {showLogin && (
        <div className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50">
          <div className="bg-gradient-to-br from-purple-900 to-indigo-900 p-6 rounded-xl shadow-2xl border border-purple-500 max-w-md w-full mx-4">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-bold text-purple-200">Вхід у систему</h2>
              <button 
                className="text-purple-300 hover:text-white text-2xl"
                onClick={() => setShowLogin(false)}
              >
                ✕
              </button>
            </div>
            <div className="space-y-4">
              <div>
                <label className="block text-purple-200 mb-2">Ім'я користувача</label>
                <input
                  type="text"
                  className={`w-full p-3 bg-black bg-opacity-50 border rounded-lg text-white placeholder-purple-300 ${
                    loginErrors.username ? 'border-red-500' : 'border-purple-500'
                  } focus:border-purple-300 focus:outline-none`}
                  value={loginForm.username}
                  onChange={(e) => setLoginForm(prev => ({...prev, username: e.target.value}))}
                  placeholder="Введіть ім'я користувача"
                />
                {loginErrors.username && (
                  <p className="text-red-400 text-sm mt-1">{loginErrors.username}</p>
                )}
              </div>
              <div>
                <label className="block text-purple-200 mb-2">Пароль</label>
                <input
                  type="password"
                  className={`w-full p-3 bg-black bg-opacity-50 border rounded-lg text-white placeholder-purple-300 ${
                    loginErrors.password ? 'border-red-500' : 'border-purple-500'
                  } focus:border-purple-300 focus:outline-none`}
                  value={loginForm.password}
                  onChange={(e) => setLoginForm(prev => ({...prev, password: e.target.value}))}
                  placeholder="Введіть пароль"
                />
                {loginErrors.password && (
                  <p className="text-red-400 text-sm mt-1">{loginErrors.password}</p>
                )}
              </div>
              <div className="flex space-x-3 pt-2">
                <button 
                  className="flex-1 py-2 bg-gray-600 hover:bg-gray-700 rounded-lg transition-colors"
                  onClick={() => setShowLogin(false)}
                >
                  Скасувати
                </button>
                <button 
                  className="flex-1 py-2 bg-gradient-to-r from-purple-600 to-indigo-600 hover:from-purple-700 hover:to-indigo-700 rounded-lg transition-all transform hover:scale-105"
                  onClick={handleLogin}
                >
                  Увійти
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      <div className="max-w-7xl mx-auto px-4 py-6">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Galaxy Map */}
          <div className="lg:col-span-2">
            <div className="bg-gradient-to-br from-purple-900 to-indigo-900 rounded-xl shadow-2xl border border-purple-500 overflow-hidden">
              <div className="p-4 border-b border-purple-500 flex justify-between items-center">
                <h2 className="text-xl font-bold text-purple-200">Мапа Галактики</h2>
                <button 
                  className="px-3 py-1 bg-purple-600 hover:bg-purple-700 rounded-lg transition-colors text-sm border border-purple-400"
                  onClick={fetchPlanets}
                >
                  🔄 Оновити
                </button>
              </div>
              <div className="relative bg-black bg-opacity-50 h-96 overflow-hidden">
                {/* Galactic Regions - концентричні кільця */}
                <div className="absolute inset-0 flex items-center justify-center">
                  {/* Deep Core */}
                  <div className="absolute w-16 h-16 border border-purple-400 rounded-full opacity-30"></div>
                  {/* Core Worlds */}
                  <div className="absolute w-32 h-32 border border-blue-400 rounded-full opacity-25"></div>
                  {/* Inner Rim */}
                  <div className="absolute w-48 h-48 border border-green-400 rounded-full opacity-20"></div>
                  {/* Mid Rim */}
                  <div className="absolute w-64 h-64 border border-yellow-400 rounded-full opacity-15"></div>
                  {/* Outer Rim */}
                  <div className="absolute w-80 h-80 border border-red-400 rounded-full opacity-10"></div>
                </div>

                {/* Region Labels */}
                <div className="absolute inset-0 text-xs text-purple-300 opacity-60">
                  <div className="absolute top-4 left-1/2 transform -translate-x-1/2">Unknown Regions</div>
                  <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2">Wild Space</div>
                  <div className="absolute top-1/2 left-4 transform -translate-y-1/2 rotate-90">Outer Rim</div>
                  <div className="absolute top-1/2 right-4 transform -translate-y-1/2 -rotate-90">Hutt Space</div>
                  <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 text-purple-400">Core</div>
                </div>
                
                {/* Stars background */}
                <div className="absolute inset-0">
                  {[...Array(150)].map((_, i) => (
                    <div
                      key={i}
                      className="absolute bg-white rounded-full animate-pulse"
                      style={{
                        left: `${Math.random() * 100}%`,
                        top: `${Math.random() * 100}%`,
                        width: `${Math.random() * 2 + 1}px`,
                        height: `${Math.random() * 2 + 1}px`,
                        animationDelay: `${Math.random() * 3}s`
                      }}
                    />
                  ))}
                </div>
                
                {/* Hyperlanes - з'єднання між планетами */}
                <svg className="absolute inset-0 w-full h-full">
                  {/* Corellian Run */}
                  <line x1="50%" y1="50%" x2="65%" y2="70%" stroke="rgba(139, 69, 19, 0.3)" strokeWidth="1" strokeDasharray="2,2" />
                  <line x1="50%" y1="50%" x2="48%" y2="45%" stroke="rgba(139, 69, 19, 0.3)" strokeWidth="1" strokeDasharray="2,2" />
                  
                  {/* Hydian Way */}
                  <line x1="40%" y1="25%" x2="70%" y2="75%" stroke="rgba(30, 144, 255, 0.3)" strokeWidth="1" strokeDasharray="2,2" />
                  
                  {/* Rimma Trade Route */}
                  <line x1="45%" y1="40%" x2="67%" y2="68%" stroke="rgba(255, 165, 0, 0.3)" strokeWidth="1" strokeDasharray="2,2" />
                </svg>
                
                {/* Planets */}
                {!loading && planets.map((planet) => {
                  const position = planetPositions[planet.name];
                  if (!position) return null;
                  
                  return (
                    <div
                      key={planet.name}
                      className="absolute transform -translate-x-1/2 -translate-y-1/2 cursor-pointer transition-all hover:scale-150 group"
                      style={{
                        left: `${position.x}%`,
                        top: `${position.y}%`
                      }}
                      onClick={() => handlePlanetClick(planet.name)}
                    >
                      <div
                        className="w-3 h-3 rounded-full shadow-lg border border-white hover:border-purple-300 animate-pulse"
                        style={{ backgroundColor: position.color }}
                      />
                      <div className="absolute -bottom-5 left-1/2 transform -translate-x-1/2 text-xs text-purple-200 whitespace-nowrap opacity-0 group-hover:opacity-100 transition-opacity bg-black bg-opacity-75 px-2 py-1 rounded">
                        {planet.name}
                        <div className="text-xs text-purple-400">{position.region}</div>
                      </div>
                    </div>
                  );
                })}
                
                {loading && (
                  <div className="absolute inset-0 flex items-center justify-center">
                    <div className="text-center">
                      <div className="w-12 h-12 border-4 border-purple-500 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
                      <p className="text-purple-200">Завантаження галактики...</p>
                    </div>
                  </div>
                )}
              </div>
            </div>

            {/* Selected Planet Details */}
            {selectedPlanet && (
              <div className="mt-6 bg-gradient-to-br from-purple-900 to-indigo-900 rounded-xl shadow-2xl border border-purple-500">
                <div className="p-4 border-b border-purple-500 flex justify-between items-center">
                  <h3 className="text-xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-yellow-300 to-orange-300">
                    {selectedPlanet.name}
                  </h3>
                  <button 
                    className="text-purple-300 hover:text-white text-xl"
                    onClick={() => setSelectedPlanet(null)}
                  >
                    ✕
                  </button>
                </div>
                <div className="p-6">
                  <p className="text-purple-100 mb-4 leading-relaxed">{selectedPlanet.description}</p>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                      <h4 className="text-purple-300 font-semibold mb-3">Основні характеристики:</h4>
                      <div className="space-y-2 text-sm">
                        <p><span className="text-purple-400">Діаметр:</span> {selectedPlanet.diameter} км</p>
                        <p><span className="text-purple-400">Орбітальний період:</span> {selectedPlanet.orbital_period} днів</p>
                        <p><span className="text-purple-400">Період обертання:</span> {selectedPlanet.rotation_period} годин</p>
                      </div>
                    </div>
                    <div>
                      <h4 className="text-purple-300 font-semibold mb-3">Умови:</h4>
                      <div className="space-y-2 text-sm">
                        <p><span className="text-purple-400">Гравітація:</span> {selectedPlanet.gravity}</p>
                        <p><span className="text-purple-400">Поверхнева вода:</span> {selectedPlanet.surface_water}%</p>
                        <p><span className="text-purple-400">Клімат:</span> {selectedPlanet.climate}</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            {/* Quiz Section */}
            <div className="bg-gradient-to-br from-purple-900 to-indigo-900 rounded-xl shadow-2xl border border-purple-500">
              <div className="p-4 border-b border-purple-500">
                <h3 className="text-lg font-bold text-purple-200">🎯 Вікторина з Зоряних Війн</h3>
              </div>
              <div className="p-6">
                {!quiz.completed ? (
                  <>
                    <div className="flex space-x-2 mb-4">
                      <span className="px-3 py-1 bg-purple-600 text-white text-sm rounded-lg">
                        Питання {quiz.currentQuestion + 1} з {quiz.questions.length}
                      </span>
                      <span className="px-3 py-1 bg-green-600 text-white text-sm rounded-lg">
                        Рахунок: {quiz.score}
                      </span>
                    </div>
                    <h4 className="text-purple-100 mb-4">{quiz.questions[quiz.currentQuestion].question}</h4>
                    <div className="space-y-2 mb-4">
                      {quiz.questions[quiz.currentQuestion].options.map((option, index) => (
                        <label key={index} className="flex items-center space-x-2 cursor-pointer p-2 rounded-lg hover:bg-purple-800 transition-colors">
                          <input
                            type="radio"
                            name="quizAnswer"
                            checked={quiz.selectedAnswer === index}
                            onChange={() => handleQuizAnswer(index)}
                            className="text-purple-600 focus:ring-purple-500"
                          />
                          <span className="text-purple-100">{option}</span>
                        </label>
                      ))}
                    </div>
                    <button 
                      className="w-full py-2 bg-gradient-to-r from-purple-600 to-indigo-600 hover:from-purple-700 hover:to-indigo-700 rounded-lg transition-all transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed disabled:hover:scale-100"
                      onClick={submitQuizAnswer}
                      disabled={quiz.selectedAnswer === null}
                    >
                      {quiz.currentQuestion < quiz.questions.length - 1 ? 'Далі' : 'Завершити'}
                    </button>
                  </>
                ) : (
                  <div className="text-center">
                    <h4 className="text-xl text-purple-100 mb-2">Вікторину завершено! 🎉</h4>
                    <p className="text-lg text-purple-200 mb-4">
                      Ваш результат: {quiz.score} з {quiz.questions.length}
                      <br/>
                      ({Math.round((quiz.score / quiz.questions.length) * 100)}%)
                    </p>
                    <button 
                      className="px-6 py-2 bg-gradient-to-r from-purple-600 to-indigo-600 hover:from-purple-700 hover:to-indigo-700 rounded-lg transition-all transform hover:scale-105"
                      onClick={resetQuiz}
                    >
                      Пройти знову
                    </button>
                  </div>
                )}
              </div>
            </div>

            {/* Survey Section */}
            <div className="bg-gradient-to-br from-purple-900 to-indigo-900 rounded-xl shadow-2xl border border-purple-500">
              <div className="p-4 border-b border-purple-500">
                <h3 className="text-lg font-bold text-purple-200">📊 Опитування</h3>
              </div>
              <div className="p-6 space-y-4">
                <div>
                  <label className="block text-purple-300 mb-2">Улюблена фракція:</label>
                  <select 
                    className="w-full p-2 bg-black bg-opacity-50 border border-purple-500 rounded-lg text-white focus:border-purple-300 focus:outline-none"
                    value={surveyData.favoriteFaction}
                    onChange={(e) => setSurveyData(prev => ({...prev, favoriteFaction: e.target.value}))}
                  >
                    <option value="">Оберіть фракцію</option>
                    <option value="jedi">Джедаї</option>
                    <option value="sith">Сіти</option>
                    <option value="rebels">Повстанці</option>
                    <option value="empire">Імперія</option>
                    <option value="republic">Республіка</option>
                  </select>
                </div>

                <div>
                  <label className="block text-purple-300 mb-2">Відвідані планети:</label>
                  <div className="space-y-2">
                    {['Tatooine', 'Coruscant', 'Naboo', 'Hoth'].map(planet => (
                      <label key={planet} className="flex items-center space-x-2 cursor-pointer">
                        <input
                          type="checkbox"
                          checked={surveyData.visitedPlanets.includes(planet)}
                          onChange={(e) => {
                            if (e.target.checked) {
                              setSurveyData(prev => ({
                                ...prev,
                                visitedPlanets: [...prev.visitedPlanets, planet]
                              }));
                            } else {
                              setSurveyData(prev => ({
                                ...prev,
                                visitedPlanets: prev.visitedPlanets.filter(p => p !== planet)
                              }));
                            }
                          }}
                          className="text-purple-600 focus:ring-purple-500"
                        />
                        <span className="text-purple-100">{planet}</span>
                      </label>
                    ))}
                  </div>
                </div>

                <div>
                  <label className="block text-purple-300 mb-2">Рівень знань галактики:</label>
                  <div className="space-y-2">
                    {['Падаван', 'Джедай', 'Майстер'].map(level => (
                      <label key={level} className="flex items-center space-x-2 cursor-pointer">
                        <input
                          type="radio"
                          name="galaxyKnowledge"
                          checked={surveyData.galaxyKnowledge === level}
                          onChange={() => setSurveyData(prev => ({...prev, galaxyKnowledge: level}))}
                          className="text-purple-600 focus:ring-purple-500"
                        />
                        <span className="text-purple-100">{level}</span>
                      </label>
                    ))}
                  </div>
                </div>

                {(surveyData.favoriteFaction || surveyData.visitedPlanets.length > 0 || surveyData.galaxyKnowledge) && (
                  <div className="bg-purple-800 bg-opacity-50 border border-purple-600 rounded-lg p-4">
                    <h4 className="text-purple-200 font-semibold mb-2">Ваші відповіді:</h4>
                    <div className="text-sm space-y-1">
                      {surveyData.favoriteFraction && <p><span className="text-purple-400">Фракція:</span> {surveyData.favoriteFraction}</p>}
                      {surveyData.visitedPlanets.length > 0 && <p><span className="text-purple-400">Планети:</span> {surveyData.visitedPlanets.join(', ')}</p>}
                      {surveyData.galaxyKnowledge && <p><span className="text-purple-400">Рівень:</span> {surveyData.galaxyKnowledge}</p>}
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer className="bg-gradient-to-r from-purple-800 to-indigo-900 text-center py-4 mt-8 border-t border-purple-500">
        <p className="text-purple-200 font-semibold">May the Force be with you!</p>
        <p className="text-purple-300 text-sm">Дані оновлюються автоматично кожні 30 секунд.</p>
      </footer>
    </div>
  );
};

export default StarWarsGalaxyMap;
